import React from 'react';
import { Container } from '@mui/material'
import { Button, TextField } from '@mui/material';
import { connect } from 'react-redux'
import { handleListValues, renderListTarefas } from '../store/action/actions'


function tarefas(props) {

  const RenderTarefas = () => {
    const list = props.list;
    return list.map((item) => (
      <div>
        <h3>{item}</h3>
      </div>
    ))
  }


  return (
    <Container>
      <TextField
        label="Insira Sua Tarefa"
        value={props.description}
        margin="normal"
        name="email"
        fullWidth
        onChange={props.handleChange}
      />


      <Button
        type="button"
        variant="contained"
        onClick={props.handleListValues}
      >Adicionar Tarefa
      </Button>

      <div>
        {RenderTarefas()}
      </div>
    </Container>

  )
}

const mapStateToProps = (state) => ({
  list: state.loguin.list,
  description: state.loguin.description
})

const mapDispatchToProps = (dispatch) => ({
  handleChange: (e) => {
    dispatch(handleListValues(e))
  },
  handleListValues: () => {
    dispatch(renderListTarefas())
  }
})



export default connect(mapStateToProps, mapDispatchToProps)(tarefas)
