const initialState = { description: '', list: [] }



const Reducers = (state = initialState, action) => {
    switch (action.type) {
        case 'INPUT_CHANGE':
            return {
                ...state,
                description: action.payload,
            }
        case 'LIST_TAREFAS':
            return {
                ...state,
                list: [...state.list, ...state.description]
            }
        default:
            return state
    }
}

export default Reducers
