import React from "react";
import { Container } from "@mui/material";
import { Button, TextField } from "@mui/material";
import { connect } from "react-redux";
import { handleListValues, renderListTarefas } from "../store/action/actions";

function tarefas(props) {
  const RenderTarefas = () => {
    const list = props.list;
    return list.map((item, index) => (
      <div key={item}>
        <h3>{item}</h3>
        <Button type="button" variant="danger" onClick={() => {}}>
          Remover Tarefa
        </Button>
      </div>
    ));
  };

  return (
    <Container>
      <TextField
        label="Insira Sua Tarefa"
        margin="normal"
        name="Tarefa"
        fullWidth
        onChange={props.handleChange}
      />

      <Button
        type="button"
        variant="contained"
        onClick={props.handleListValues}
      >
        Adicionar Tarefa
      </Button>

      <div>{RenderTarefas()}</div>
    </Container>
  );
}

const mapStateToProps = (state) => ({
  list: state.loguin.list,
  description: state.loguin.description,
  description2: state.loguin.description2,
});

const mapDispatchToProps = (dispatch) => ({
  handleChange: (e) => {
    dispatch(handleListValues(e));
  },
  handleListValues: () => {
    dispatch(renderListTarefas());
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(tarefas);
