const initialState = {
  list: [],
  email: "",
  password: "",
  redirect: false,
  description: "",
};

const Reducers = (state = initialState, action) => {
  switch (action.type) {
    case "LIST_TAREFAS":
      return {
        ...state,
        list: [...state.list, state.description],
      };
    case "LOGUIN_INPUT":
      return {
        ...state,
        email: action.payload,
      };
    case "LOGUIN_INPUT2":
      return {
        ...state,
        password: action.payload,
      };
    case "REDIRECT":
      return {
        ...state,
        redirect: action.payload,
      };
    case "VALUE_INPUT_TAREFAS":
      return {
        ...state,
        description: action.payload,
      };
    default:
      return state;
  }
};

export default Reducers;
