import React from "react";
import { Container } from "@mui/material";
import { Button, TextField } from "@mui/material";
import { connect } from "react-redux";
import { handleListValues, renderListTarefas, removeTarefas } from "../store/action/actions";

function tarefas(props) {
  const list = props.list;
  console.log(list);

  return (
    <Container>
      <TextField
        label="Insira Sua Tarefa"
        margin="normal"
        name="Tarefa"
        fullWidth
        onChange={props.handleChange}
      />

      <Button
        type="button"
        variant="contained"
        onClick={props.handleListValues}
      >
        Adicionar Tarefa
      </Button>
      {list.map((item, index) => (
        <div key={item}>
          <h3>{item[0]}</h3>
          <p style={{display: 'none'}}>{item[1]}</p>
          <Button
            type="button"
            variant="outlined"
            color="error"
            onClick={() => props.removeTarefas(item[2])}
          >
            Remover Tarefa
          </Button>
        </div>
      ))}

    </Container>
  );
}

const mapStateToProps = (state) => ({
  list: state.loguin.list,
  description: state.loguin.description,
  description2: state.loguin.description2,
});

const mapDispatchToProps = (dispatch) => ({
  handleChange: (e) => {
    dispatch(handleListValues(e));
  },
  handleListValues: () => {
    dispatch(renderListTarefas());
  },
  removeTarefas: (id) => {
    dispatch(removeTarefas(id));
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(tarefas);
