import { Button, TextField } from '@mui/material';
import { Container } from '@mui/material'
import { Redirect } from 'react-router-dom'
import React, { Component } from 'react';
import { connect } from 'react-redux'
import { inputChange } from '../store/action/actions'

class Form extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            redirect: false,
            password: '',
        }

    }



    render() {
        const { email, password, redirect } = this.state;
        return (

            <div>
                <Container component={'article'} max-width="small">
                    <h1>Loguin</h1>
                    <TextField
                        label="email"
                        value={email}
                        margin="normal"
                        name="email"
                        fullWidth
                        onChange={() => this.props.handleChange}
                    />

                    <TextField
                        label="Password"
                        onChange={() => this.props.handleChange}
                        value={password}
                        name="password"
                        margin="normal"
                        fullWidth
                    />

                    <Button
                        type="button"
                        variant="contained"
                        onClick={() => {
                            if (email === 'Pedro.goulart09@icloud.com' && password === '1234') {
                                this.setState({ redirect: true })
                            }

                        }}
                    >Sign-in</Button>
                    {redirect && <Redirect to="/tarefas" />}




                </Container>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    handle: state.loguin.description
})

const mapDispatchToProps = (dispatch) => ({
    handleChange: () => {
        const action = inputChange(dispatch)
        dispatch(action)
    }
})



export default connect(mapStateToProps, mapDispatchToProps)(Form)
